(window.webpackJsonp=window.webpackJsonp||[]).push([[252],{1125:function(n,h,c){"use strict";c.r(h);var t=c(7),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"_18-hoc-hoi-khong-ngung"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#_18-hoc-hoi-khong-ngung"}},[n._v("#")]),n._v(" 18: Học hỏi không ngừng")]),n._v(" "),c("p",[n._v("Chúng ta đang sống trong những khoảng thời gian thú vị. Khi sự phát triển được phân phối trên toàn cầu, bạn học được rằng có rất nhiều người có khả năng làm công việc của bạn. Bạn cần tiếp tục học hỏi để ở lại với thị trường. Nếu không bạn sẽ trở thành một con khủng long bị mắc kẹt. Cho đến một ngày, trong cùng một công việc, bạn sẽ không còn cần thiết nữa hay công việc của bạn sẽ được chuyển giao cho một số nguồn rẻ hơn.\nVậy bạn nên làm gì với nó? Một số nhà tuyển dụng đủ hào phóng để cung cấp, đào tạo để mở rộng bộ kỹ năng của bạn. Những người khác có thể không có thời gian rảnh rỗi hay tiền bạc cho bất kì đào tạo nào cả. Vậy để thận trọng, bạn nên có trách nhiệm hơn với việc học tập của mình.")]),n._v(" "),c("p",[n._v("Đây là các cách để bạn có thể tiếp tục học tập. Nhiều trong số này có thể tìm thấy trên Internet:")]),n._v(" "),c("ul",[c("li",[n._v("Đọc sách 📙, tạp chí, blog và trên các websites. Nếu bạn muốn đi sâu hơn vào môn một chủ đề, hãy xem xét gia nhập một nhóm cùng đam mê.")]),n._v(" "),c("li",[n._v("Nếu bạn thực sự muốn được đắm mình vào một công nghệ, hãy bắt tay vào viết một số code.")]),n._v(" "),c("li",[n._v("Luôn luôn cố gắng làm việc với người cố vấn, vì là người đứng đầu có thể cản trở giáo dục của bạn. Mặc dù bạn có thể học được một số điều từ bất kì ai, nhưng bạn có thể học hỏi nhiều hơn từ một người thông minh hoặc có kinh nghiệm hơn. Nếu bạn không thể tìm kiếm một người cố vấn, hãy cân nhắc việc tiếp tục.")]),n._v(" "),c("li",[n._v("Sử dụng những cố vấn ảo, Tìm kiếm tác giả và những nhà phát triển trên mạng, những người mà bạn thật sự thích và đọc mọi thứ họ viết. Hãy theo dõi blog của họ.")]),n._v(" "),c("li",[n._v("Nhận biết những frameworks và các thư viện bạn sử dụng. Tìm hiểu cách thức hoạt động của nó để bạn có thể sử dụng nó tốt hơn. Nếu họ chia sẻ mã nguồn, bạn thật sự may mắn. Sử dụng các debugger chuyển qua code để xem những gì đang diễn ra.")]),n._v(" "),c("li",[n._v("Bất cứ khi nào bạn mắc lỗi, sửa lỗi hoặc gặp sự cố, hãy cố gắng thật sự hiểu những gì đang diễn ra. Cũng có khả năng là có người khác cũng gặp lỗi đó và đã đăng nó lên mạng tìm sự giúp đỡ. Vậy nên google thật sự hữu ích lúc này.")]),n._v(" "),c("li",[n._v("Cách tốt nhất để học một cái gì đó là dạy và nói về nó. Khi người khác lắng nghe và đặt câu hỏi cho bạn, bạn sẽ cảm thấy có động lực hơn để học. Hãy thử ăn trưa và học khi làm việc, kết bạn với những người có cùng mục tiêu hoặc đến các hội thảo liên quan.")]),n._v(" "),c("li",[n._v("Tham gia hoặc bắt đầu một nhóm nghiên cứu(cộng đồng mẫu) về ngôn ngữ, công nghệ mà bạn quan tâm.")]),n._v(" "),c("li",[n._v("Đi đến các hội thảo. Hoặc nếu bạn không thể đi thì có rất nhiều hội thảo được phát trực tuyến miễn phí.")]),n._v(" "),c("li",[n._v("Đi lại có thể làm cho một vài thứ dễ dàng, nhẹ nhàng hơn.")]),n._v(" "),c("li",[n._v("Hiểu những phân tích và cảnh báo khi sử dụng các static analysis tools trong IDE.")]),n._v(" "),c("li",[n._v("Làm theo lời khuyên của các lập trình viên thực dụng và mỗi năm hãy học một ngôn ngữ mới. Học ít nhất một công nghệ hoặc công cụ mới. Sự chia nhỏ ra cung cấp cho bạn những ý tưởng có thể sử dụng trong kho công nghệ hiện tại của mình.")]),n._v(" "),c("li",[n._v("Không phải tất cả những gì bạn học phải là về công nghệ. Tìm hiểu lĩnh vực bạn đang làm việc để hiểu rõ hơn các yêu cầu và giúp đỡ giải quyết vấn đề kinh doanh. Học cách làm việc hiệu quả hơn — làm thế nào để làm việc tốt hơn là một sự lựa chọn tốt.")]),n._v(" "),c("li",[n._v("Quay trở lại trường học 🎓.")])]),n._v(" "),c("p",[n._v("Sẽ thật tuyệt khi có khả năng mà Neo có trong The Matrix, chúng ta sẽ có nhiều thời gian hơn cho bản thân mình không dành quá nhiều cho công việc và học tập.")]),n._v(" "),c("p",[n._v("Công nghệ thay đổi nhanh chóng. Đừng để bị tụt lại phía sau.")])])}),[],!1,null,null,null);h.default=i.exports}}]);