(window.webpackJsonp=window.webpackJsonp||[]).push([[311],{1306:function(n,h,t){"use strict";t.r(h);var i=t(7),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"_49-hoc-ngoai-ngu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#_49-hoc-ngoai-ngu"}},[n._v("#")]),n._v(" 49: Học Ngoại Ngữ")]),n._v(" "),t("p",[n._v("Lập trình viên cũng cần phải giao tiếp nhiều.")]),n._v(" "),t("p",[n._v("Cuộc đời của một lập trình viên dường như đa số là làm việc với máy tính. Cụ thể là với các chương trình chạy trên máy. Cuộc giao tiếp này cho việc thể hiện ý tưởng theo cách có thể đọc bằng máy. Đây là một triển vọng hứng khởi: Các chương trình đều trở nên hiện thực hóa từ những ý tưởng mà không có sự tham gia của chất vật lí nào.")]),n._v(" "),t("p",[n._v("Lập trình viên cần phải lưu loát trong ngôn ngữ của máy móc, dù là thật hay ảo, và các ý tưởng chung liên quan đến ngôn ngữ ấy đều thông qua các công cụ phát triển. Học thêm nhiều khái niệm trừu tượng là điều quan trọng, nếu không thì một vài ý tưởng sẽ trở nên rất khó để thể hiện. Một người lập trình giỏi cần có khả năng ra khỏi thói quen hằng ngày, nhận thức được các ngôn ngữ khác nhau sẽ thể hiện các mục đích khác nhau. Khi bạn làm được, thời cơ sẽ đến.")]),n._v(" "),t("p",[n._v("Ngoài giao tiếp với máy móc thì người lập trình cần phải giao tiếp với đồng nghiệp của họ. Ngày nay các dự án lớn là thành quả của sự nỗ lực tập thể hơn chỉ đơn giản là một ứng dụng nghệ thuật của lập trình. Quan trọng là phải hiểu và diễn đạt nhiều hơn những các khái niệm trừu tượng có thể đọc bằng máy. Những người lập trình viên giỏi nhất tôi từng biết hầu như đều thông thạo tiếng mẹ đẻ của họ, cũng như các ngôn ngữ thông dụng khác. Đây không chỉ là về giao tiếp với người khác: nói được một ngôn ngữ thành thạo sẽ mang lại sự suy nghĩ rõ ràng không thể bỏ qua khi trừu tượng hóa một vấn đề. Và đây cũng là vấn đề của việc lập trình.")]),n._v(" "),t("p",[n._v("Bên cạnh giao tiếp với máy móc, bản thân và đồng nghiệp, một dự án vẫn còn có những người hỗ trợ khác, đa số là có nền tảng kĩ thuật khác nhau hoặc không có. Họ sống với việc thử nghiệm, chất lượng và triển khai, với tiếp thị và bán hàng, họ là những người dùng cuối cùng ở vài công sở (hoặc cửa hàng hoặc nhà ở). Bạn phải hiểu họ và các mối quan tâm của họ. Điều này gần như không thể nếu bạn không thể nói ngôn ngữ của họ — ngôn ngữ trong thế giới, lĩnh vực của họ. Trong khi bạn nghĩ rằng mình đã có một cuộc trao đổi rất ổn với họ, họ chắc chắn không nghĩ thế.")]),n._v(" "),t("p",[n._v("Nếu bạn nói chuyện với nhân viên kế toán, bạn cần có kiến thức cơ bản về cost-center, về vốn bất động, vốn làm việc và những thứ khác. Nếu như bạn nói chuyện với tiếp thị hoặc luật sư, một vài thuật ngữ và ngôn ngữ ( và suy nghĩ của họ) sẽ trở nên quen thuộc với bạn. Những ngôn ngữ chuyên ngành đó cần được thông thạo lưu loát bởi một người trong dự án — lập trình viên là những người lí tưởng. Lập trình viên là những người chịu trách nhiệm cuối cùng cho việc đưa các ý tưởng từ máy tính đến đời sống thực tiễn.")]),n._v(" "),t("p",[n._v("Và, tất nhiên, cuộc sống là những gì nhiều hơn máy tính phần mềm có. Theo ghi chép của Charlemagne, biết được một ngôn ngữ đồng nghĩa với việc hiểu được một tâm hồn. Đối với những mối liên hệ của bạn ngoài ngành công nghiệp phần mềm, việc biết ngoại ngữ sẽ được đánh giá cao. Để biết khi nào nên lắng nghe hơn là nói. Để biết được hầu hết các ngôn ngữ đều không dùng đến lời nói.")]),n._v(" "),t("blockquote",{staticClass:"highlight-quote"},[t("p",[t("em",[n._v("Whereof one cannot speak, thereof one must be silent.")]),n._v("  — Ludwig Wittgenstein")])]),t("p",[t("em",[n._v("(Cái gì người ta không thể nói về, người ta phải im lặng về nó.)")])])])}),[],!1,null,null,null);h.default=g.exports}}]);