(window.webpackJsonp=window.webpackJsonp||[]).push([[345],{1269:function(n,h,t){"use strict";t.r(h);var c=t(7),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"_93-viet-code-nhu-the-ban-phai-ho-tro-no-đen-het-đoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#_93-viet-code-nhu-the-ban-phai-ho-tro-no-đen-het-đoi"}},[n._v("#")]),n._v(" 93: Viết code như thể bạn phải hỗ trợ nó đến hết đời")]),n._v(" "),t("p",[n._v("Bạn có thể hỏi 97 người liệu lập trình viên nên biết gì và làm gì, và bạn sẽ nhận được 97 câu trả lời khác nhau. Điều này có thể vừa áp đảo vừa đáng sợ. Mọi lời khuyên đều tốt, mọi nguyên tắc đều hợp lý, và mọi câu chuyện đều hấp dẫn, nhưng bạn nên bắt đầu từ đâu? Quan trọng hơn, một khi bạn bắt đầu, làm thế nào để theo kịp những thực tiễn tốt nhất bạn đã học được và biến chúng thành một phần không thể thiếu trong quá trình thực hành lập trình của bạn?")]),n._v(" "),t("p",[n._v("Tôi nghĩ câu trả lời nằm trong suy nghĩ của bạn hoặc, rõ ràng hơn, trong thái độ của bạn. Nếu bạn không quan tâm đến nhà phát triển, tester, quản lý, người tiếp thị và bán hàng cũng như người dùng, thì bạn sẽ không bị thúc đẩy sử dụng Phát triển dựa trên thử nghiệm hoặc viết comment rõ ràng trong code của bạn. Tôi nghĩ có một cách đơn giản để điều chỉnh thái độ của bạn và tạo động lực để cung cấp sản phẩm với chất lượng tốt nhất:")]),n._v(" "),t("p",[n._v("Viết code như thể bạn phải hỗ trợ nó cho đến hết cuộc đời.")]),n._v(" "),t("p",[n._v("Nếu bạn chấp nhận khái niệm này, những điều tuyệt vời sẽ đến. Nếu bạn chấp nhận rằng bất kỳ nhà tuyển dụng nào - cả trong quá khứ lẫn hiện tại có quyền gọi cho bạn vào lúc nửa đêm, yêu cầu bạn giải thích các lựa chọn bạn đã thực hiện khi viết phương pháp fooBar, bạn sẽ dần cải thiện để trở thành một lập trình viên chuyên nghiệp. Bạn sẽ tự nhiên nghĩ ra những phương thức và biến tốt hơn. Bạn sẽ tránh được việc viết những code dài hàng trăm dòng. Bạn sẽ tìm kiếm, tìm hiểu và sử dụng design pattern. Bạn sẽ viết comment, kiểm tra và tái cấu trúc liên tục code của bạn. Hỗ trợ tất cả code bạn từng viết cho đến hết đời cũng là một nỗ lực có thể mở rộng. Do đó, bạn không có lựa chọn nào khác ngoài việc trở nên tốt hơn, thông minh hơn và hiệu quả hơn.")]),n._v(" "),t("p",[n._v("Những code bạn viết nhiều năm trước vẫn ảnh hưởng đến sự nghiệp của bạn, dù bạn có thích hay không. Bạn để lại dấu vết về kiến thức, thái độ, sự kiên trì, tính chuyên nghiệp, mức độ cam kết và mức độ thích thú trên mọi phương pháp, class hay module bạn thiết kế và viết. Mọi người sẽ hình thành ý kiến về bạn dựa trên những code mà họ thấy. Nếu những ý kiến đó tiêu cực, bạn sẽ nhận được ít hơn từ sự nghiệp của bạn hơn bạn mong muốn. Hãy chăm chút sự nghiệp của bạn, của khách hàng và của người dùng với mọi dòng code- viết code như thể bạn phải hỗ trợ nó cho đến giây phút cuối cùng của cuộc đời mình.")])])}),[],!1,null,null,null);h.default=i.exports}}]);