(window.webpackJsonp=window.webpackJsonp||[]).push([[366],{1537:function(n,h,t){"use strict";t.r(h);var c=t(7),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"_37-cham-chi-chua-chac-thanh-cong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#_37-cham-chi-chua-chac-thanh-cong"}},[n._v("#")]),n._v(" 37: Chăm chỉ chưa chắc thành công")]),n._v(" "),t("p",[n._v("Là một lập trình viên, làm việc chăm chỉ thường không được đền đáp. Bạn có thể tự lừa dối bản thân và đồng nghiệp rằng bạn đang đóng góp rất nhiều cho một dự án bằng cách dành nhiều giờ ở văn phòng. Nhưng sự thật là chỉ cần làm ít, bạn có thể đạt được nhiều- thậm chí nhiều hơn nữa. Nếu bạn đang cố gắng tập trung và “"),t("em",[n._v("làm việc hiệu quả")]),n._v("” hơn 30 giờ một tuần thì có lẽ bạn đang làm việc quá sức. Bạn nên xem xét giảm khối lượng công việc để làm việc được hiệu quả hơn và hoàn thành nhiều việc hơn.")]),n._v(" "),t("p",[n._v("Kết luận này nghe có vẻ trái nghịch và thậm chí còn gây tranh cãi, nhưng nó là hệ quả trực tiếp của thực tế là việc lập trình và phát triển phần mềm nói chung là cả một quá trình học tập không ngừng nghỉ. Khi bạn triển khai một dự án, bạn sẽ hiểu vấn đề hơn và tìm ra những cách hiệu quả hơn để đạt được mục tiêu. Để tránh những việc thừa thãi, bạn phải dành thời gian quan sát ảnh hưởng của những gì bạn đang làm- phản ánh qua những gì bạn nhìn thấy và thay đổi hành vi của bạn cho phù hợp.")]),n._v(" "),t("p",[n._v("Lập trình chuyên nghiệp thường không giống như chạy vài km, với mục tiêu nằm ở ngay cuối con đường. Hầu hết các dự án phần mềm giống như một cuộc đua marathon trong- bóng- tối, với chỉ một tấm bản đồ sơ sài. Nếu bạn chỉ hướng về một hướng và chạy nhanh nhất có thể, bạn có thể gây ấn tượng với một số người, nhưng bạn không có khả năng thành công. Bạn cần duy trì một tốc độ bền vững và bạn cần điều chỉnh ít nhiều khi tìm hiểu về vị trí của bản thân cũng như vạch đích mà bạn đang hướng tới.")]),n._v(" "),t("p",[n._v("Ngoài ra, bạn luôn cần tìm hiểu thêm về phát triển phần mềm nói chung và kỹ thuật lập trình nói riêng. Bạn có thể đọc sách, tham dự hội nghị, giao tiếp với các chuyên gia, thử nghiệm các kỹ thuật mới và tìm hiểu về các công cụ giúp đơn giản hóa công việc của bạn. Là một lập trình viên chuyên nghiệp, bạn phải luôn tự cập nhật lĩnh vực chuyên môn của mình — giống như các bác sĩ phẫu thuật và phi công luôn phải cập nhật lĩnh vực chuyên môn riêng của họ. Bạn cần dành buổi tối, cuối tuần và ngày lễ để tự học, do đó khoảng thời gian này không thể dành để làm thêm giờ cho dự án hiện tại của bạn. Liệu bạn có mong các bác sĩ phẫu thuật dành 60 giờ một tuần trong phòng mổ, hay phi công bay đến 60 giờ một tuần? Tất nhiên là không, vì sự chuẩn bị và giáo dục là một phần thiết yếu trong nghề nghiệp của họ.")]),n._v(" "),t("p",[n._v("Hãy tập trung vào dự án, đóng góp nhiều nhất có thể bằng cách tìm những giải pháp thông minh, cải thiện kỹ năng của bạn, thể hiện những gì bạn làm và điều chỉnh hành vi của bạn. Tránh tự bôi xấu bản thân bằng cách cư xử như một con chuột đồng trong một cái lồng quay.")]),n._v(" "),t("p",[n._v("Là một lập trình viên chuyên nghiệp, bạn nên biết rằng cố gắng tập trung và “"),t("strong",[n._v("làm việc hiệu quả")]),n._v("” 60 giờ một tuần không phải điều hợp lý. Hãy hành động như một chuyên gia: chuẩn bị, thực hiện, quan sát, phản ánh và thay đổi.")])])}),[],!1,null,null,null);h.default=i.exports}}]);