(window.webpackJsonp=window.webpackJsonp||[]).push([[368],{1546:function(n,h,t){"use strict";t.r(h);var c=t(7),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"_52-hay-đe-du-an-cua-ban-tu-len-tieng"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#_52-hay-đe-du-an-cua-ban-tu-len-tieng"}},[n._v("#")]),n._v(" 52: Hãy để dự án của bạn tự lên tiếng")]),n._v(" "),t("p",[n._v("Dự án của bạn có thể có một hệ thống kiểm soát thích hợp. Có thể nó được kết nối với một máy chủ tích hợp liên tục xác minh tính chính xác bằng hàng loạt các kiểm tra tự động. Đó là một điều tốt.")]),n._v(" "),t("p",[n._v("Bạn có thể đưa các công cụ phân tích code tĩnh vào máy chủ của mình để thu thập dữ liệu. Các dữ liệu này cung cấp phản hồi về các khía cạnh cụ thể trong code của bạn, cũng như sự phát triển của chúng theo thời gian. Khi bạn cài đặt dữ liệu, sẽ luôn có một ranh giới mà bạn không muốn vượt qua. Ví dụ khi bạn bắt đầu với phạm vi 20%, bạn không bao giờ muốn tụt xuống mức 15%. Tích hợp liên tục giúp bạn theo dõi những con số này, nhưng bạn vẫn cần kiểm tra thường xuyên. Hãy tưởng tượng bạn ủy thác nhiệm vụ này cho chính dự án và khi mọi thứ trở nên tồi tệ hơn, bạn cũng dựa vào nó để báo cáo.")]),n._v(" "),t("p",[n._v("Dự án của bạn cần có tiếng nói riêng. Điều này có thể thực hiện qua email 📮 hay tin nhắn thông báo cho các nhà phát triển về sự suy giảm hoặc cải thiện mới nhất của số liệu. Nhưng nó thậm chí còn hiệu quả hơn khi thể hiện dự án của bạn bằng cách sử dụng một thiết bị phản hồi cực độ (XFD).")]),n._v(" "),t("p",[n._v("Ý tưởng của XFD là vận hành một thiết bị như đèn, vòi nước di động, robot đồ chơi hay thậm chí là máy phóng tên lửa USB, dựa trên kết quả phân tích tự động. Bất cứ khi nào giới hạn của bạn bị phá vỡ, thiết bị sẽ thay đổi trạng thái của nó. Trong trường hợp của bóng đèn, nó sẽ sáng lên rõ ràng. Bạn sẽ không bỏ lỡ thông điệp ngay cả khi bạn đang vội vã ra về.")]),n._v(" "),t("p",[n._v("Tùy thuộc vào loại thiết bị phản hồi mà bạn có thể nghe thấy tiếng ngắt, nhìn thấy các tín hiệu cảnh báo đỏ hay thậm chí “ngửi” thấy “mùi code”. Các thiết bị có thể được nhân rộng tại các địa điểm khác nhau nếu bạn làm việc trong một nhóm phân phối. Bạn có thể đặt đèn giao thông trong văn phòng của bạn, biểu thị trạng thái tổng thể của dự án. Quản lý của bạn sẽ đánh giá cao điều đó.")]),n._v(" "),t("p",[n._v("Hãy để óc sáng tạo dẫn dắt trong việc lựa chọn một thiết bị phù hợp. Nếu phong cách của bạn có chút lập dị, bạn có thể trang bị cho linh vật của đội mình những món đồ điều khiển bằng radio. Nếu bạn muốn một phong cách chuyên nghiệp hơn, hãy đầu tư vào những chiếc đèn thiết kế bóng bẩy. Tìm kiếm trên Internet để có thêm cảm hứng. Bất cứ thứ gì có phích cắm hay điều khiển từ xa đều có tiềm năng sử dụng như một thiết bị phản hồi cực độ.")]),n._v(" "),t("p",[n._v("Thiết bị phản hồi cực độ hoạt động như hộp thoại trong dự án của bạn. Dự án ấy hiện đang được các nhà phát triển phàn nàn hoặc khen ngợi theo các tiêu chuẩn mà họ đã đề ra. Bạn có thể thúc đẩy việc nhân cách hóa này bằng cách áp dụng phần mềm tổng hợp giọng nói và một cặp loa 🔊. Bây giờ thì dự án của bạn đã thực sự lên tiếng.")])])}),[],!1,null,null,null);h.default=i.exports}}]);