(window.webpackJsonp=window.webpackJsonp||[]).push([[376],{1551:function(n,t,h){"use strict";h.r(t);var c=h(7),i=Object(c.a)({},(function(){var n=this,t=n.$createElement,h=n._self._c||t;return h("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[h("h1",{attrs:{id:"_40-hay-cai-đat-phan-mem-nay"}},[h("a",{staticClass:"header-anchor",attrs:{href:"#_40-hay-cai-đat-phan-mem-nay"}},[n._v("#")]),n._v(" 40: Hãy cài đặt phần mềm này")]),n._v(" "),h("p",[n._v("Tôi không phải là không có hứng thú về chương trình của bạn")]),n._v(" "),h("p",[n._v("Tôi đang ngập mặt trong một đống công việc với một "),h("strong",[n._v("to-do list")]),n._v(" dài cả thước. Lý do duy nhất tôi truy cập vào website của bạn là vì tôi nghe nói, từ một nguồn cũng chẳng đáng tin cậy, rằng phần mềm của bạn sẽ giải quyết hết mọi vấn đề của tôi. Vậy nên hãy thứ lỗi nếu tôi có hơi kỹ tính một chút.")]),n._v(" "),h("p",[n._v("Theo cách đọc thông thường, tôi thường đọc tiêu đề và đi tìm dòng chữ được đánh dấu bằng một đường gạch chân màu xanh với nội dung “"),h("em",[n._v("download now")]),n._v("”. Thêm nữa, nếu tôi truy cập trang này bằng trình duyệt Linux và IP đến từ Anh Quốc, tôi hẳn nhiên muốn thấy các sản phẩm với phiên bản dành cho Linux và được cung cấp từ một máy chủ ở Châu Âu. Ngay lúc đó nếu có một hộp thoại tập tin mở ra, tôi chắc chắn sẽ nhấn lệnh tải tập tin xuống trước, rồi sau đó mới đọc các nội dung còn lại.")]),n._v(" "),h("p",[n._v("Chúng ta vẫn cứ hay trình bày dài dòng về lý do, mục đích của mọi thứ mà chúng ta đã thực hiện. Trong khi chỉ cần project của bạn khiến tôi không vừa ý dù chỉ một giây, tôi sẵn sàng vứt nó đi để tìm cái khác. Sự đáp ứng nhu cầu ngay lập tức là quan trọng nhất.")]),n._v(" "),h("p",[n._v("Thử thách đầu tiên là cài đặt. Chớ nghĩ rằng nó không thành vấn đề. Hãy thử đến thư mục tải về xem. Nó có phải toàn là file zip và tar? Bạn đã giải nén bao nhiêu trong số chúng rồi? Và Có bao nhiêu file thực sự hữu dụng? Nếu bạn cũng nghĩ như tôi, có lẽ chỉ 1/3 chúng thực sự làm được gì đó có ích hơn là chỉ chiếm bộ nhớ.")]),n._v(" "),h("p",[n._v("Mặc dù tôi rất thích sự tiện lợi, nhưng tôi cũng không cho phép ai khác vào nhà tôi mà chưa được mời. Trước khi khi kích hoạt cài đặt tôi muốn biết chính xác nơi bạn sẽ đặt ứng dụng của bạn trên máy tính của tôi. Và tôi cũng muốn nó được cài đặt tinh gọn nhất có thể. Hơn nữa, tôi cũng sẽ không bao giờ cho phép nó được cài đặt nếu như tôi cảm thấy nó không thể bị gỡ bỏ ngay khi tôi thất vọng về nó. Máy tính tôi đang chạy rất ổn, và tôi muốn nó luôn ổn như vậy.")]),n._v(" "),h("p",[n._v("Nếu chương trình của bạn là ứng dụng giao diện người dùng, thì tôi chỉ muốn thấy những thao tác đơn giản và kết quả của chúng. Đừng nói về wizards, tôi chẳng hiểu biết gì về cách nó hoạt động. Điều tôi muốn đơn giản là đọc hoặc ghi file. Tôi không thích phải tạo project mới, import thư viện hay phải khai báo email 📮. Và sau cùng, đừng quên viết hướng dẫn sử dụng.")]),n._v(" "),h("p",[n._v("Nếu chương trình của bạn là một thư viện, tôi sẽ cần tìm trong web page của bạn mục quick start guide. Tôi muốn website của bạn phải trình bày kỹ thuật in ra cụm từ “Hello World” ngắn gọn trong vòng 5 dòng, và phải tường minh, dễ hiểu. Chỉ cần một đoạn script đơn giản thôi, chứ đừng bắt tôi phải điền vào một tệp XML hoặc template nào quá lớn. Hãy nhớ rằng tôi có thể chọn tải về một framework đối thủ của bạn. Và bạn cũng hiểu rằng luôn có một ai đó ngoài kia đang cố gắng trở nên xuất sắc hơn bạn ngoài cộng đồng. Và sau cùng, tất nhiên, đừng quên viết hướng dẫn sử dụng.")]),n._v(" "),h("p",[n._v("Và sẽ luôn có một hướng dẫn sử dụng, đúng chứ?! Cái thứ có chức năng chỉ dẫn tôi bằng ngôn ngữ tôi có thể hiểu.")]),n._v(" "),h("p",[n._v("Nếu mục hướng dẫn sử dụng của bạn giải quyết đúng vấn đề của tôi, tôi sẽ phấn khích vô cùng. Tôi sẽ đọc cách làm thế nào để khởi động các chức năng của nó trong một tâm trạng vui vẻ. Tôi có thể thư giãn và làm một tách trà — vâng, vì tôi người Anh mà!! — sau đó thử các ví dụ và học cách dùng sản phẩm của bạn. Nếu vấn đề của tôi được giải quyết, tôi sẽ gửi bạn lời cảm ơn, tôi còn có thể sẽ báo cáo bạn một số lỗi hay đề nghị những tính năng mới. Tôi thậm chí sẽ khoe với bạn bè tôi rằng phần mềm của bạn tuyệt với cỡ nào, và chẳng bao giờ bận tâm đến những phần mềm khác của đối thủ. Tất cả là vì bạn đã hoàn toàn chinh phục trải nghiệm đầu tiên của tôi. Và tôi có lý do gì để hồ nghi bạn được?")])])}),[],!1,null,null,null);t.default=i.exports}}]);