(window.webpackJsonp=window.webpackJsonp||[]).push([[417],{1595:function(n,h,t){"use strict";t.r(h);var c=t(7),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"_97-khach-hang-khong-chac-chan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#_97-khach-hang-khong-chac-chan"}},[n._v("#")]),n._v(" 97: Khách Hàng Không Chắc Chắn")]),n._v(" "),t("p",[n._v("Những Gì Họ Nói - Tôi vẫn chưa gặp được người khách hàng nào có thể thoải mái nói tôi nghe những gì họ muốn, hay cung cấp nhiều thông tin. Vấn đề là khách hàng sẽ ít khi nói toàn bộ sự thật. Thường thì họ không nói dối, nhưng họ trò chuyện với vị trí khách hàng chứ không phải là một nhà phát triển. Họ sử dụng từ ngữ và bối cảnh của họ và bỏ qua những chi tiết cần thiết. Họ tự cho rằng bạn đã làm việc ở công ty được 20 năm, giống như họ vậy. Điều này cũng có nghĩa là họ không thực sự biết rằng mình muốn gì! Một vài người có thể hiểu “tình hình” rõ hơn, nhưng họ hiếm khi có khả năng nói lại chi tiết những gì họ nhận thấy. Có thể những người khác sẽ không có cái nhìn hoàn thiện, nhưng họ biết họ cần gì. Như vậy, sao bạn có thể chắc chắn giao một dự án phần mềm cho người thậm chí còn không nói với bạn toàn bộ những gì họ muốn? Đơn giản thôi.")]),n._v(" "),t("p",[t("strong",[n._v("Hãy tương tác với họ nhiều hơn.")])]),n._v(" "),t("p",[n._v("Hãy đưa ra các yêu cầu với khách hàng ngay từ đầu và thường xuyên. Đừng chỉ thuật lại những gì họ muốn bằng lời của họ. Hãy nhớ rằng: Họ không chắc chắn những gì họ yêu cầu bạn đâu. Tôi thường thay đổi một số từ trong cuộc trò chuyện và quan sát phản ứng của họ. Bạn sẽ rất kinh ngạc bởi số lần những customer và client có ý tưởng hoàn toàn khác nhau. Song người mà nói với bạn những gì anh ta muốn trong dự án phần mềm của anh ta sẽ sử dụng lộn xộn các thuật ngữ và mong bạn theo dõi những gì anh ta đang đề cập đến. Bạn sẽ cảm thấy lúng túng và điều này sẽ ảnh hưởng đến phần mềm bạn đang viết.")]),n._v(" "),t("p",[n._v("Hãy thảo luận các vấn đề với khách hàng của bạn nhiều lần trước khi cho rằng bạn thực sự hiểu họ cần gì. Cố gắng đề cập vấn đề với họ hai hoặc ba lần. Trò chuyện về những thứ diễn ra trước và sau chủ đề mà bạn và họ đang thảo luận sẽ khiến không khí trở nên tốt hơn. Nếu có thể, hãy nhờ nhiều người kể cho bạn nghe chủ đề đó trong các cuộc giao tiếp khác nhau. Nghe những câu chuyện khác nhau từ họ sẽ giúp bạn biết được những điều bạn muốn. Hai người cùng nói về một chủ đề thường sẽ trở nên mâu thuẫn với nhau. Tốt nhất là bạn nên thảo luận về những bất đồng và đưa ra kết luận trước khi bạn bắt đầu công việc với phần mềm cực-kì-phức-tạp của bạn.")]),n._v(" "),t("p",[n._v("Hãy sử dụng các vật dụng trực quan trong cuộc đối thoại. Bạn có thể làm việc này đơn giản như dùng một tấm bảng trắng trong cuộc gặp mặt, hay dễ như giai đoạn tạo giao diện nền trong thiết kế, hay phức tạp như tạo một nguyên bản hoạt động. Làm những việc trên trong suốt quá trình giao tiếp sẽ làm thu hút sự chú ý lâu dài và tăng khả năng ghi nhớ thông tin. Biết được điều này sẽ giúp cho dự án của bạn tốt hơn nhiều đấy.")]),n._v(" "),t("p",[n._v("Trước đây, khi tôi là “lập trình viên đa phương tiện” của một nhóm sản xuất các dự án lớn. Một khách hàng đã mô tả rất nhiều quan điểm và cảm xúc của họ về dự án. Bảng màu chung được thảo luận trong cuộc đối thoại đã chọn nền màu đen cho buổi thuyết trình. Chúng tôi đã nghĩ mọi việc đã xong. Nhóm thiết kế đồ họa đã nhanh chóng cho ra hàng trăm tập tin lớp đồ họa. Chúng tôi mất rất nhiều thời gian để chỉnh sửa ở các công đoạn cuối cùng. Vào ngày ra mắt sản phẩm với người khách ấy, và chúng tôi đã rất hoảng hốt. Khi cô ấy nhìn thấy sản phẩm, chính xác những gì cô ấy nói là “Khi tôi nói màu đen, thì ý của tôi là màu trắng.” Như bạn đã thấy đấy, sẽ không bao giờ có quan điểm rõ ràng đâu.")])])}),[],!1,null,null,null);h.default=g.exports}}]);